import React, { useState } from "react";

let titleRef =React.createRef();
let desRef = React.createRef();
let priceRef =React.createRef();
const AddProduct =()=>{
    const [file,setFile]= useState();
    return(
        <div className="container-fluid bg-dark p-5">
            <h2 className="pl-5"> <i className="fa-solid fa-circle-plus"></i> Add New Product</h2>
            <form>
  <div className="form-group">
    <label for="exampleInputEmail1">Title</label>
    <input type="text" className="form-control" ref={titleRef} id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Title"/>
    <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
  </div>

  <div className="form-group">
    <label for="exampleInputEmail1">Description</label>
    <textarea className="form-control" ref={desRef}></textarea>
 </div>

 <div className="form-group">
    <label for="exampleInputEmail1">Price</label>
    <input type="text" className="form-control" ref={priceRef} id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Price"/>
  </div>

  <div className="custom-file">
  <input type="file" onChange={(e)=> setFile({file:e.target.files[0]})}className="custom-file-input" id="customFile"/>
  <label className="custom-file-label" for="customFile">Choose file</label>
</div>
 <br/><br/>
  <button type="submit" onClick={(e)=> sendData(file,e)} className="btn btn-primary"> <i className="fa-solid fa-circle-plus"></i> Save</button>
</form>
        </div>
    )
}

function sendData(file,e){
    e.preventDefault();
   let dataValue ={
    title:titleRef.current.value,
    des:desRef.current.value,
    price:priceRef.current.value
   }
   console.log(dataValue)
   console.log(file)
}
export default AddProduct;
